task main()
{
	int speedleft = 0;
	int speedright = 20;
	int time = 1000;

		while(speedleft <= 80)
		{
			motor[motorB] = speedleft + 40;
			motor[motorC] = speedright + 50;
			wait1Msec(time);

			speedleft = speedleft + 3;
			speedright = speedright + 2;
			time = time + 500;
		}

}
